import React, {createContext, useContext} from 'react';
import {Route, Routes} from "react-router-dom";
import PageLayout from "./Components/PageLayout/pageLayout";
import Homepage from "./Components/Home/home";
import LoginPage from "./Components/Login/login";
import HomepagePost from './Components/Posts/HomepagePost/homepagePost';
import {createTheme, ThemeProvider} from "@mui/material/styles";
import {PaletteMode, useMediaQuery} from "@mui/material";
import getDesignTokens from "./Components/PageLayout/theme";
import { Post } from "./Components/Posts/posts"

export const ColorModeContext = React.createContext({
    toggleColorMode: () => {
    }
});

function App() {
    const [mode, setMode] = React.useState<PaletteMode>('dark');
    const colorMode = React.useMemo(
        () => ({
            // The dark mode switch would invoke this method
            toggleColorMode: () => {
                setMode((prevMode: PaletteMode) =>
                    prevMode === 'light' ? 'dark' : 'light',
                );
            },
        }),
        [],
    );

    // Update the theme only if the mode changes
    const theme = React.useMemo(() => createTheme(getDesignTokens(mode)), [mode]);


    // !! DELETE THIS: dummy post for homepage posts
    const [postt, setPostt] = React.useState(
        { 
            title:'Post title',
            content: "Lorem ipsum fumeaza mult",
            creatorId: "string",
            kudos: 0,
            mediaURL: ["string"],
            createdAt: "2023-11-18T14:33:24.280Z",
            updatedAt: "2023-11-18T14:33:24.280Z",
            _id: "string",
            __v: 0
         }
    );

    return (
        <ColorModeContext.Provider value={colorMode}>
            <ThemeProvider theme={theme}>
                <Routes>
                    <Route element={<PageLayout/>}>
                        <Route path="/" element={<Homepage/>}/>
                    </Route>
                    <Route path="login" element={<LoginPage/>}/>'

                    {/* ! delete this route */}

                    <Route path="homepagePost" element={
                        <HomepagePost
                            post= { 
                                postt
                            }
                        />
                    }/> 

                </Routes>
            </ThemeProvider>
        </ColorModeContext.Provider>
    );
}

export default App;
